import React, { useState } from 'react';
import { products, Product } from '../data/products';
import ProductCard from '../components/ProductCard';
import './ShopPage.css';

const ShopPage: React.FC = () => {
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [searchTerm, setSearchTerm] = useState<string>('');

  const categories = [
    { id: 'all', name: 'All Products' },
    { id: 'floral', name: 'Floral' },
    { id: 'woody', name: 'Woody' },
    { id: 'citrus', name: 'Citrus' },
    { id: 'oriental', name: 'Oriental' },
    { id: 'fresh', name: 'Fresh' }
  ];

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         product.inspiration.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearch;
  });

  return (
    <div className="shop-page">
      {/* Hero Section */}
      <section className="shop-hero">
        <div className="container">
          <div className="shop-hero-content">
            <h1>Our Collection</h1>
            <p className="shop-subtitle">
              Locally crafted fragrances made in Vermont. Each scent is carefully inspired by 
              luxury perfumes and created with premium ingredients at an affordable price.
            </p>
            <div className="made-in-badge">
              <span>üèîÔ∏è Made in Vermont</span>
            </div>
          </div>
        </div>
      </section>

      {/* Filters Section */}
      <section className="shop-filters">
        <div className="container">
          <div className="filters-content">
            <div className="search-box">
              <input
                type="text"
                placeholder="Search fragrances..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <svg className="search-icon" width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <circle cx="11" cy="11" r="8"></circle>
                <path d="m21 21-4.35-4.35"></path>
              </svg>
            </div>

            <div className="category-filters">
              {categories.map(category => (
                <button
                  key={category.id}
                  className={`filter-btn ${selectedCategory === category.id ? 'active' : ''}`}
                  onClick={() => setSelectedCategory(category.id)}
                >
                  {category.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </section>

      {/* Products Grid */}
      <section className="products-section">
        <div className="container">
          <div className="products-header">
            <h2>Perfumes</h2>
            <p className="products-count">
              {filteredProducts.length} {filteredProducts.length === 1 ? 'product' : 'products'}
            </p>
          </div>

          <div className="products-grid">
            {filteredProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>

          {filteredProducts.length === 0 && (
            <div className="no-products">
              <p>No products found matching your criteria.</p>
              <button 
                className="btn btn-primary"
                onClick={() => {
                  setSelectedCategory('all');
                  setSearchTerm('');
                }}
              >
                View All Products
              </button>
            </div>
          )}
        </div>
      </section>

      {/* Newsletter Section */}
      <section className="newsletter-section">
        <div className="container">
          <div className="newsletter-content">
            <h3>Connect with Us Today</h3>
            <p>Get updates on new fragrances and exclusive offers</p>
            <form className="newsletter-form">
              <input 
                type="email" 
                placeholder="Enter Your Email Here"
                className="newsletter-input"
              />
              <button type="submit" className="btn btn-primary">Subscribe</button>
            </form>
            <label className="newsletter-checkbox">
              <input type="checkbox" />
              <span>Yes, Subscribe me to get updates on Sunrise Scents</span>
            </label>
          </div>
        </div>
      </section>
    </div>
  );
};

export default ShopPage;
