import React from 'react';
import './FeaturedCollections.css';

interface Collection {
  id: string;
  name: string;
  description: string;
  image: string;
  gradient: string;
}

const collections: Collection[] = [
  {
    id: 'sunrise',
    name: 'Sunrise Collection',
    description: 'Warm, golden fragrances that capture the first light of dawn',
    image: '/api/placeholder/300/400',
    gradient: 'linear-gradient(135deg, #8B9A7A 0%, #D4A574 100%)'
  },
  {
    id: 'floral',
    name: 'Floral Elegance',
    description: 'Delicate blooms and romantic florals for every occasion',
    image: '/api/placeholder/300/400',
    gradient: 'linear-gradient(135deg, #E8B4B4 0%, #F5F5DC 100%)'
  },
  {
    id: 'woody',
    name: 'Woody Essence',
    description: 'Rich, sophisticated scents with deep woody undertones',
    image: '/api/placeholder/300/400',
    gradient: 'linear-gradient(135deg, #8B7355 0%, #D4A574 100%)'
  },
  {
    id: 'citrus',
    name: 'Citrus Fresh',
    description: 'Bright, energizing scents with zesty citrus notes',
    image: '/api/placeholder/300/400',
    gradient: 'linear-gradient(135deg, #FFD700 0%, #FFA500 100%)'
  },
  {
    id: 'oriental',
    name: 'Oriental Mystique',
    description: 'Exotic, warm spices and luxurious oriental blends',
    image: '/api/placeholder/300/400',
    gradient: 'linear-gradient(135deg, #8B4513 0%, #CD853F 100%)'
  },
  {
    id: 'fresh',
    name: 'Vermont Fresh',
    description: 'Clean, crisp scents inspired by Vermont\'s natural beauty',
    image: '/api/placeholder/300/400',
    gradient: 'linear-gradient(135deg, #87CEEB 0%, #98FB98 100%)'
  }
];

const FeaturedCollections: React.FC = () => {
  return (
    <section id="collections" className="featured-collections section">
        <div className="section-header fade-in">
          <h2>Featured Collections</h2>
          <p>Discover our signature fragrance families, each crafted to tell a unique story</p>
        </div>

        <div className="collections-grid">
          {collections.map((collection, index) => (
            <div 
              key={collection.id} 
              className="collection-card fade-in"
              style={{ animationDelay: `${0.2 * (index + 1)}s` }}
            >
              <div className="card-image">
                <div 
                  className="image-placeholder"
                  style={{ background: collection.gradient }}
                >
                  <div className="perfume-silhouette"></div>
                </div>
                <div className="card-overlay">
                  <button className="view-collection-btn">
                    View Collection
                  </button>
                </div>
              </div>

              <div className="card-content">
                <h3>{collection.name}</h3>
                <p>{collection.description}</p>
                <a href={`/collections/${collection.id}`} className="learn-more">
                  Learn More â†’
                </a>
              </div>
            </div>
          ))}
        </div>
    </section>
  );
};

export default FeaturedCollections;
