import React, { useState, useEffect } from 'react';
import { Product } from '../data/products';
import './ProductEditor.css';

const ProductEditor: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  // Load products from localStorage or use default data
  useEffect(() => {
    const savedProducts = localStorage.getItem('sunrise-products');
    if (savedProducts) {
      setProducts(JSON.parse(savedProducts));
    } else {
      // Load default products
      import('../data/products').then(module => {
        setProducts(module.products);
        localStorage.setItem('sunrise-products', JSON.stringify(module.products));
      });
    }
  }, []);

  const saveProducts = (updatedProducts: Product[]) => {
    setProducts(updatedProducts);
    localStorage.setItem('sunrise-products', JSON.stringify(updatedProducts));
  };

  const handleSaveProduct = (product: Product) => {
    const updatedProducts = editingProduct
      ? products.map(p => p.id === product.id ? product : p)
      : [...products, { ...product, id: product.name.toLowerCase().replace(/\s+/g, '-') }];

    saveProducts(updatedProducts);
    setEditingProduct(null);
    setShowAddForm(false);
  };

  const handleDeleteProduct = (productId: string) => {
    if (window.confirm('Are you sure you want to delete this product? This cannot be undone.')) {
      const updatedProducts = products.filter(p => p.id !== productId);
      saveProducts(updatedProducts);
    }
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.category.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="product-editor">
      <div className="editor-header">
        <div>
          <h2>Product Management</h2>
          <p>Add, edit, or remove your perfume products</p>
        </div>
        <button 
          className="btn btn-primary add-product-btn"
          onClick={() => setShowAddForm(true)}
        >
          + Add New Product
        </button>
      </div>

      <div className="search-section">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      <div className="products-grid">
        {filteredProducts.map(product => (
          <div key={product.id} className="product-card-admin">
            <div className="product-image-admin">
              <div className="image-placeholder-admin">
                <span>üß¥</span>
              </div>
            </div>

            <div className="product-info-admin">
              <h3>{product.name}</h3>
              <p className="product-price-admin">10ml: ${product.prices['10ml'].toFixed(2)} | 30ml: ${product.prices['30ml'].toFixed(2)}</p>
              <p className="product-category-admin">{product.category}</p>
              <p className="product-inspiration-admin">{product.inspiration}</p>
              <p className="product-description-admin">{product.description}</p>
            </div>

            <div className="product-actions">
              <button 
                className="btn-edit"
                onClick={() => setEditingProduct(product)}
              >
                ‚úèÔ∏è Edit
              </button>
              <button 
                className="btn-delete"
                onClick={() => handleDeleteProduct(product.id)}
              >
                üóëÔ∏è Delete
              </button>
            </div>
          </div>
        ))}
      </div>

      {filteredProducts.length === 0 && searchTerm && (
        <div className="no-results">
          <p>No products found matching "{searchTerm}"</p>
        </div>
      )}

      {(editingProduct || showAddForm) && (
        <ProductForm
          product={editingProduct}
          onSave={handleSaveProduct}
          onCancel={() => {
            setEditingProduct(null);
            setShowAddForm(false);
          }}
        />
      )}
    </div>
  );
};

interface ProductFormProps {
